import React from "react"
import classes from "./SearchBar.module.css"
import { useNavigate} from "react-router-dom";
import { useRef } from "react";
import { useEffect } from "react";
import { useState } from "react";
export default function SearchBar() {

    const searchRef = useRef();
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true);
    const [loadedCountry, setLoadedCountry] = useState([]);
    let dataCountry = [];
    
    function onSearch(event) {
        event.preventDefault();
        const enteredSearch = searchRef.current.value;
        console.log(enteredSearch)
        navigate('/country', {state: enteredSearch})
    }

    function onCountrySelected(country) {
        navigate('/country', {state: country})
    }

    function isCountry(countryObj, countryName) {
        return countryObj.name.common === countryName;
    }

    useEffect(() => {
        const url = "https://restcountries.com/v3.1/all/"
        fetch(url ,
        ).then(response => {
            return response.json();
        }).then(data => {
            setIsLoading(false);
            dataCountry = data;
            const countryNames = data.map((country) => country = country.name.common)
            const countryFlags = data.map((country) => country = country.flags.svg)
            setLoadedCountry(countryNames)
            console.log(countryNames);
            });
    }, []);

    if(isLoading) {
        return(
            <p>Loading...</p>
        )
    }

    return(
        <div className={classes.container}>
            <div className={classes.searchContainer}>
                <input autoComplete="off" id="search" ref={searchRef} className={classes.searchInput} type="text" placeholder="Find a country"></input>
                <span>
                    <img src="" alt=""></img>
                </span>
                <button onClick={onSearch}>Search</button>
            </div>
            <ul className={classes.suggestions}>
                {loadedCountry.map((option) => {
                    return(
                        <button onClick={() => onCountrySelected(option)} className={classes.dropdownItem}>
                            {option}
                            <img src={dataCountry.find(() => dataCountry, option).flags.svg}></img>
                        </button>
                    )
                })}
            </ul>

                
        </div>
    )
}
