import React from "react"
import classes from "./SearchBar.module.css"
import { useNavigate} from "react-router-dom";
import { useRef } from "react";
import { useEffect } from "react";
import { useState } from "react";
export default function SearchBar() {

    const searchRef = useRef();
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true);
    const [loadedCountry, setLoadedCountry] = useState([]);
    
    function onSearch(event) {
        event.preventDefault();
        const enteredSearch = searchRef.current.value;
        console.log(enteredSearch)
        navigate('/country', {state: enteredSearch})
    }

    useEffect(() => {
        const url = "https://restcountries.com/v3.1/all/"
        fetch(url ,
        ).then(response => {
            return response.json();
        }).then(data => {
            console.log(data);
            setIsLoading(false);
            setLoadedCountry(
                data.map((country) => country.name.common
                )
            );
        });
    }, []);

    if(isLoading) {
        return(
            <p>Loading...</p>
        )
    }

    return(
        <div>
            <div className={classes.searchContainer}>
                <input autocomplete="off" id="search" ref={searchRef} className={classes.searchInput} type="text" placeholder="Find a country"></input>
                <span>
                    <img src="" alt=""></img>
                </span>
                <button onClick={onSearch}>Search</button>

                
            </div>
        </div>
    )
}
