import React from "react"
import classes from "./SearchBar.module.css"
import { Link} from "react-router-dom";
import { useRef } from "react";
export default function SearchBar() {

    const searchRef = useRef();
    let enteredSearch = "jpn";
    
    function onSearch(event) {
        event.preventDefault();
        enteredSearch = searchRef.current.value;
        console.log(enteredSearch)
    }
    return(
        <div>
            <div className={classes.searchContainer}>
                <input id="search" ref={searchRef} className={classes.searchInput} type="text" placeholder="Find a country"></input>
                <span>
                    <img src="" alt=""></img>
                </span>

                    <button onClick={onSearch}>
                        <Link to="/country" state={enteredSearch} handler={onSearch}>Search</Link>
                    </button>
                
            </div>
        </div>
    )
}
