import React from "react"
import classes from "./CountryInfo.module.css"
import { useState } from "react";
import { useEffect } from "react";

export default function CountryInfo() {
    const [isLoading, setIsLoading] = useState(true);
    const [loadedCountry, setLoadedCountry] = useState([]);

    useEffect(() => {
        fetch("https://restcountries.com/v3.1/alpha/us",
        ).then(response => {
            return response.json();
        }).then(data => {
            console.log(data);
            setIsLoading(false);
            setLoadedCountry(data);
        });
    }, []);

    if(isLoading) {
        return(
            <p>Loading...</p>
        )
    }

    return(
        <div>
            <div className={classes.topSection} >
                <img className={classes.flag} src={loadedCountry[0].flags.svg} alt="flag"></img>
                <h2>{loadedCountry[0].name.common} ({loadedCountry[0].name.official})</h2>
            </div>
            <div className={classes.infoContainer}>
                <div className={classes.infoColumn}>
                    <div className={classes.info}>
                        <h3>Continent:</h3>
                        <p>{loadedCountry[0].continents[0]}</p>
                    </div>
                    <div className={classes.info}>
                        <h3>Capital:</h3>
                        <p>{loadedCountry[0].capital}</p>
                    </div>
                    <div className={classes.info}>
                        <h3>Language:</h3>
                        <p>{Object.keys(loadedCountry[0].languages[0])}</p>
                    </div>
                    <div className={classes.info}>
                        <h3>Population:</h3>
                        <p>{loadedCountry[0].population}</p>
                    </div>
                </div>
                <div className={classes.infoColumn}></div>
            </div>
        </div>
    )


    }
